INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (867,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','eclipse-birt','x86_64','3975686','2.6.0','1.1.el6','2013-11-27 23:52:03','Eclipse-based reporting system','BIRT is an Eclipse-based open source reporting system for web applications,\nespecially those based on Java and J2EE. BIRT has two main components:\na report designer based on Eclipse, and a runtime component that you\ncan add to your app server. BIRT also offers a charting engine that lets\nyou add charts to your own application.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (868,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','boost-devel','x86_64','45823086','1.41.0','11.el6_1.2','2013-11-27 23:55:06','The Boost C++ headers and shared development libraries','Headers and shared object symlinks for the Boost C++ libraries.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (869,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','desktop-file-utils','x86_64','143573','0.15','9.el6','2013-11-27 23:49:12','Utilities for manipulating .desktop files','.desktop files are used to describe an application for inclusion in\nGNOME or KDE menus.  This package contains desktop-file-validate which\nchecks whether a .desktop file complies with the specification at\nhttp://www.freedesktop.org/standards/, and desktop-file-install\nwhich installs a desktop file to the standard directory, optionally\nfixing it up in the process.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (870,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','pycairo-devel','x86_64','7396','1.8.6','2.1.el6','2013-11-27 23:50:43','Libraries and headers for pycairo','This package contains files required to build wrappers for cairo add-on\nlibraries so that they interoperate with pycairo.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (871,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','sgml-common','noarch','135735','0.6.3','32.el6','2013-11-27 23:49:36','Common SGML catalog and DTD files','The sgml-common package contains a collection of entities and DTDs\nthat are useful for processing SGML, but that don\'t need to be\nincluded in multiple packages.  Sgml-common also includes an\nup-to-date Open Catalog file.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (872,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libgnome-devel','x86_64','577561','2.28.0','11.el6','2013-11-27 23:52:58','Libraries and headers for libgnome','\nGNOME (GNU Network Object Model Environment) is a user-friendly set of\nGUI applications and desktop tools to be used in conjunction with a\nwindow manager for the X Window System. The libgnome-devel package\nincludes the libraries and include files that you will need to\nuse libgnome.\n\nYou should install the libgnome-devel package if you would like to\ncompile GNOME applications. You do not need to install libgnome-devel\nif you just want to use the GNOME desktop environment.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (873,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gnome-themes','noarch','13215383','2.28.1','6.el6','2013-11-27 23:52:06','Themes for GNOME','The gnome-themes package contains a collection of desktop themes for GNOME.\nThese themes can change the appearance of application widgets, icons, window\nborders, cursors, etc.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (874,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','alsa-lib-devel','x86_64','13332583','1.0.22','3.el6','2013-11-27 23:55:08','Development files from the ALSA library','The Advanced Linux Sound Architecture (ALSA) provides audio and MIDI\nfunctionality to the Linux operating system.\n\nThis package includes the ALSA development libraries for developing\nagainst the ALSA libraries and interfaces.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (875,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libexif','x86_64','1949111','0.6.21','5.el6_3','2013-11-27 23:49:12','Library for extracting extra information from image files','Most digital cameras produce EXIF files, which are JPEG files with\nextra tags that contain information about the image. The EXIF library\nallows you to parse an EXIF file and read the data from those tags.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (876,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','kernel-devel','x86_64','24943706','2.6.32','358.el6','2013-11-27 23:50:50','Development package for building kernel modules to match the kernel','This package provides kernel headers and makefiles sufficient to build modules\nagainst the kernel package.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (877,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libarchive','x86_64','307255','2.8.3','4.el6_2','2013-11-27 23:49:38','A library for handling streaming archive formats','Libarchive is a programming library that can create and read several different\nstreaming archive formats, including most popular tar variants, several cpio\nformats, and both BSD and GNU ar variants. It can also write shar archives and\nread ISO9660 CDROM images and ZIP archives.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (878,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','compiz','x86_64','4493475','0.8.2','24.el6','2013-11-27 23:53:05','OpenGL window and compositing manager','Compiz is one of the first OpenGL-accelerated compositing window\nmanagers for the X Window System. The integration allows it to perform\ncompositing effects in window management, such as a minimization\neffect and a cube workspace.  Compiz is an OpenGL compositing manager\nthat use Compiz use EXT_texture_from_pixmap OpenGL extension for\nbinding redirected top-level windows to texture objects.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (879,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gnome-desktop','x86_64','3162907','2.28.2','11.el6.centos','2013-11-27 23:52:07','Shared code among gnome-panel, gnome-session, nautilus, etc','\nThe gnome-desktop package contains an internal library\n(libgnomedesktop) used to implement some portions of the GNOME\ndesktop, and also some data files and other shared components of the\nGNOME user environment.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (880,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','audit','x86_64','979444','2.2','2.el6','2013-11-27 23:55:11','User space tools for 2.6 kernel auditing','The audit package contains the user space utilities for\nstoring and searching the audit records generate by\nthe audit subsystem in the Linux 2.6 kernel.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (881,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','which','x86_64','73004','2.19','6.el6','2013-11-27 23:49:13','Displays where a particular program in your path is located','The which command shows the full pathname of a specified program, if\nthe specified program is in your PATH.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (882,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','eggdbus','x86_64','321013','0.6','3.el6','2013-11-27 23:50:51','Experimental D-Bus bindings for GObject','Experimental D-Bus bindings for GObject.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (883,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','xdg-utils','noarch','212283','1.0.2','17.20091016cvs.el6','2013-11-27 23:49:41','Basic desktop integration functions','The xdg-utils package is a set of simple scripts that provide basic\ndesktop integration functions for any Free Desktop, such as Linux.\nThey are intended to provide a set of defacto standards.\nThis means that:\n*  Third party software developers can rely on these xdg-utils\n   for all of their simple integration needs.\n*  Developers of desktop environments can make sure that their\n   environments are well supported\n*  Distribution vendors can provide custom versions of these utilities\n\nThe following scripts are provided at this time:\n* xdg-desktop-menu      Install desktop menu items\n* xdg-desktop-icon      Install icons to the desktop\n* xdg-icon-resource     Install icon resources\n* xdg-mime              Query information about file type handling and\n                        install descriptions for new file types\n* xdg-open              Open a file or URL in the user\'s preferred application\n* xdg-email             Send mail using the user\'s preferred e-mail composer\n* xdg-screensaver       Control the screensaver\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (884,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','crda','x86_64','31721','1.1.1_2010.11.22','1.el6','2013-11-27 23:53:10','Regulatory compliance daemon for 802.11 wireless networking','CRDA acts as the udev helper for communication between the kernel\nand userspace for regulatory compliance. It relies on nl80211\nfor communication. CRDA is intended to be run only through udev\ncommunication from the kernel.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (885,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gtkspell','x86_64','80614','2.0.16','1.el6','2013-11-27 23:52:07','On-the-fly spell checking for GtkTextView widgets','GtkSpell provides word-processor-style highlighting and replacement of\nmisspelled words in a GtkTextView widget as you type. Right-clicking a\nmisspelled word pops up a menu of suggested replacements.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (886,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','perl-XML-Dumper','noarch','41749','0.81','6.el6','2013-11-27 23:55:14','Perl module for dumping Perl objects from/to XML','XML::Dumper dumps Perl data to XML format. XML::Dumper can also read\nXML data that was previously dumped by the module and convert it back\nto Perl.  Perl objects are blessed back to their original packaging;\nif the modules are installed on the system where the perl objects are\nreconstituted from xml, they will behave as expected. Intuitively, if\nthe perl objects are converted and reconstituted in the same\nenvironment, all should be well.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (887,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libxslt','x86_64','2223955','1.1.26','2.el6_3.1','2013-11-27 23:49:13','Library providing the Gnome XSLT engine','This C library allows to transform XML files into other XML files\n(or HTML, text, ...) using the standard XSLT stylesheet transformation\nmechanism. To use it you need to have a version of libxml2 >= 2.6.27\ninstalled. The xsltproc command is a command line interface to the XSLT engine\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
