INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (889,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libssh2','x86_64','324941','1.4.2','1.el6','2013-11-27 23:49:42','A library implementing the SSH2 protocol','libssh2 is a library implementing the SSH2 protocol as defined by\nInternet Drafts: SECSH-TRANS(22), SECSH-USERAUTH(25),\nSECSH-CONNECTION(23), SECSH-ARCH(20), SECSH-FILEXFER(06)*,\nSECSH-DHGEX(04), and SECSH-NUMBERS(10).\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
