INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (892,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','bison','x86_64','2081518','2.4.1','5.el6','2013-11-27 23:55:15','A GNU general-purpose parser generator','Bison is a general purpose parser generator that converts a grammar\ndescription for an LALR(1) context-free grammar into a C program to\nparse that grammar. Bison can be used to develop a wide range of\nlanguage parsers, from ones used in simple desk calculators to complex\nprogramming languages. Bison is upwardly compatible with Yacc, so any\ncorrectly written Yacc grammar should work with Bison without any\nchanges. If you know Yacc, you shouldn\'t have any trouble using\nBison. You do need to be very proficient in C programming to be able\nto use Bison. Bison is only needed on systems that are used for\ndevelopment.\n\nIf your system will be used for C development, you should install\nBison.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
