INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (901,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','slang','x86_64','1800725','2.2.1','1.el6','2013-11-27 23:49:46','The shared library for the S-Lang extension language','S-Lang is an interpreted language and a programming library.  The\nS-Lang language was designed so that it can be easily embedded into\na program to provide the program with a powerful extension language.\nThe S-Lang library, provided in this package, provides the S-Lang\nextension language.  S-Lang\'s syntax resembles C, which makes it easy\nto recode S-Lang procedures in C if you need to.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (902,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','yp-tools','x86_64','171212','2.9','12.el6','2013-11-27 23:53:13','NIS (or YP) client programs','The Network Information Service (NIS) is a system which provides\nnetwork information (login names, passwords, home directories, group\ninformation) to all of the machines on a network.  NIS can enable\nusers to login on any machine on the network, as long as the machine\nhas the NIS client programs running and the user\'s password is\nrecorded in the NIS passwd database.  NIS was formerly known as Sun\nYellow Pages (YP).\n\nThis package\'s NIS implementation is based on FreeBSD\'s YP and is a\nspecial port for glibc 2.x and libc versions 5.4.21 and later.  This\npackage only provides the NIS client programs.  In order to use the\nclients, you\'ll need to already have an NIS server running on your\nnetwork. An NIS server is provided in the ypserv package.\n\nInstall the yp-tools package if you need NIS client programs for machines\non your network.  You will also need to install the ypbind package on\nevery machine running NIS client programs.  If you need an NIS server,\nyou\'ll need to install the ypserv package on one machine on the network.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (903,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libdaemon','x86_64','58693','0.14','1.el6','2013-11-27 23:52:09','Library for writing UNIX daemons','libdaemon is a lightweight C library which eases the writing of UNIX daemons.\nIt consists of the following parts:\n* A wrapper around fork() which does the correct daemonization\n  procedure of a process\n* A wrapper around syslog() for simpler and compatible log output to\n  Syslog or STDERR\n* An API for writing PID files\n* An API for serializing UNIX signals into a pipe for usage with\n  select() or poll()\n* An API for running subprocesses with STDOUT and STDERR redirected\n  to syslog.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (904,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libitm','x86_64','187455','4.7.0','5.1.1.el6','2013-11-27 23:55:15','The GNU Transactional Memory library','This package contains the GNU Transactional Memory library\nwhich is a GCC transactional memory support runtime library.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (905,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','grep','x86_64','802765','2.6.3','3.el6','2013-11-27 23:49:15','Pattern matching utilities','The GNU versions of commonly used grep utilities. Grep searches through\ntextual input for lines which contain a match to a specified pattern and then\nprints the matching lines. GNU\'s grep utilities include grep, egrep and fgrep.\n\nGNU grep is needed by many scripts, so it shall be installed on every system.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (906,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','m17n-contrib-hindi','noarch','15817','1.1.10','4.el6_1.1','2013-11-27 23:50:53','Contributed input maps for Hindi','This package provides contributed multilingualization (m17n) input maps\nfor Hindi.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (907,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libxml2-devel','x86_64','8922872','2.7.6','8.el6_3.4','2013-11-27 23:49:49','Libraries, includes, etc. to develop XML and HTML applications','Libraries, include files, etc you can use to develop XML applications.\nThis library allows to manipulate XML files. It includes support\nto read, modify and write XML and HTML files. There is DTDs support\nthis includes parsing and validation even with complex DtDs, either\nat parse time or later once the document has been modified. The output\ncan be a simple SAX stream or and in-memory DOM like representations.\nIn this case one can use the built-in XPath and XPointer implementation\nto select subnodes or ranges. A flexible Input/Output mechanism is\navailable, with existing HTTP and FTP modules and combined to an\nURI library.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (908,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','ebtables','x86_64','261522','2.0.9','6.el6','2013-11-27 23:53:15','Ethernet Bridge frame table administration tool','Ethernet bridge tables is a firewalling tool to transparently filter network\ntraffic passing a bridge. The filtering possibilities are limited to link\nlayer filtering and some basic filtering on higher network layers.\n\nThis tool is the userspace control for the bridge and ebtables kernel\ncomponents.\n\nThe ebtables tool can be used together with the other Linux filtering tools,\nlike iptables. There are no known incompatibility issues.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (909,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libvpx','x86_64','414960','0.9.0','8.el6_0','2013-11-27 23:52:12','VP8 Video Codec SDK','libvpx provides the VP8 SDK, which allows you to integrate your applications\nwith the VP8 video codec, a high quality, royalty free, open source codec\ndeployed on millions of computers and devices worldwide.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (910,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','wqy-zenhei-fonts','noarch','16794196','0.9.45','3.el6','2013-11-27 23:55:20','WenQuanYi Zen Hei CJK Font','\nWenQuanYi Zen Hei is a Hei-Ti style (sans-serif type) Chinese\noutline font. It is designed for general purpose text formatting\nand on-screen display of Chinese characters and symbols from\nmany other languages. The embolden strokes of the font glyphs\nproduces enhanced screen contrast, making it easier to read\nrecognize. The embedded bitmap glyphs further enhance on-screen\nperformance, which can be enabled with the provided configuration\nfiles. WenQuanYi Zen Hei provides a rather complete coverage to\nChinese Hanzi glyphs, including both simplified and traditional\nforms. The total glyph number in this font is over 35,000, including\nover 21,000 Chinese Hanzi. This font has full coverage to GBK(CP936)\ncharset, CJK Unified Ideographs, as well as the code-points\nneeded for zh_cn, zh_sg, zh_tw, zh_hk, zh_mo, ja (Japanese)\nand ko (Korean) locales for fontconfig. Starting from version\n0.8, this font package has contained two font families, i.e.\nthe proportionally-spaced Zen Hei, and a mono-spaced face\nnamed \"WenQuanYi Zen Hei Mono\".\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (911,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','shared-mime-info','x86_64','1411492','0.70','4.el6','2013-11-27 23:49:15','Shared MIME information database','This is the freedesktop.org shared MIME info database.\n\nMany programs and desktops use the MIME system to represent the types of\nfiles. Frequently, it is necessary to work out the correct MIME type for\na file. This is generally done by examining the file\'s name or contents,\nand looking up the correct MIME type in a database.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
