INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (914,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','selinux-policy','noarch','9728038','3.7.19','195.el6','2013-11-27 23:53:18','SELinux policy configuration','SELinux Reference Policy - modular.\nBased off of reference policy: Checked out revision  2.20091117\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (915,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','pywebkitgtk','x86_64','190103','1.1.6','3.el6','2013-11-27 23:52:14','Python Bindings for WebKit-gtk','The purpose of pywebkitgtk is to bring an alternative web engine to\nPython/GTK+ application developers who might need a web browser engine for\ntheir next application or developers wishing to have a better browser engine\nthat they can access to using the Python programming language.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (916,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','abyssinica-fonts','noarch','649794','1.0','5.1.el6','2013-11-27 23:55:37','SIL Abyssinica fonts','\nSIL Abyssinica is a Unicode typeface family containing glyphs for the\nEthiopic script.\n\nThe Ethiopic script is used for writing many of the languages of Ethiopia and\nEritrea. Abyssinica SIL supports all Ethiopic characters which are in Unicode\nincluding the Unicode 4.1 extensions. Some languages of Ethiopia are not yet\nable to be fully represented in Unicode and, where necessary, we have included\nnon-Unicode characters in the Private Use Area (see Private-use (PUA)\ncharacters supported by Abyssinica SIL).\n\nAbyssinica SIL is based on Ethiopic calligraphic traditions. This release is\na regular typeface, with no bold or italic version available or planned.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (917,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','apr-util','x86_64','202360','1.3.9','3.el6_0.1','2013-11-27 23:49:16','Apache Portable Runtime Utility library','The mission of the Apache Portable Runtime (APR) is to provide a\nfree library of C data structures and routines.  This library\ncontains additional utility interfaces for APR; including support\nfor XML, LDAP, database interfaces, URI parsing and more.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
