INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (922,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','vlgothic-fonts','noarch','3843798','20091202','2.el6','2013-11-27 23:55:54','Japanese TrueType font','\nVLGothic provides Japanese TrueType fonts from the Vine Linux project.\nMost of the glyphs are taken from the M+ and Sazanami Gothic fonts,\nbut some have also been improved by the project.\n\nThis package provides the monospace VLGothic font.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (923,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','procps','x86_64','465911','3.2.8','25.el6','2013-11-27 23:49:16','System and process monitoring utilities','The procps package contains a set of system utilities that provide\nsystem information. Procps includes ps, free, skill, pkill, pgrep,\nsnice, tload, top, uptime, vmstat, w, watch and pdwx. The ps command\ndisplays a snapshot of running processes. The top command provides\na repetitive update of the statuses of running processes. The free\ncommand displays the amounts of free and used memory on your\nsystem. The skill command sends a terminate command (or another\nspecified signal) to a specified set of processes. The snice\ncommand is used to change the scheduling priority of specified\nprocesses. The tload command prints a graph of the current system\nload average to a specified tty. The uptime command displays the\ncurrent time, how long the system has been running, how many users\nare logged on, and system load averages for the past one, five,\nand fifteen minutes. The w command displays a list of the users\nwho are currently logged on and what they are running. The watch\nprogram watches a running program. The vmstat command displays\nvirtual memory statistics about processes, memory, paging, block\nI/O, traps, and CPU activity. The pwdx command reports the current\nworking directory of a process or processes.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
