INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (943,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','policycoreutils','x86_64','3635718','2.0.83','19.30.el6','2013-11-27 23:52:29','SELinux policy core utilities','Security-enhanced Linux is a feature of the Linux® kernel and a number\nof utilities with enhanced security functionality designed to add\nmandatory access controls to Linux.  The Security-enhanced Linux\nkernel contains new architectural components originally developed to\nimprove the security of the Flask operating system. These\narchitectural components provide general support for the enforcement\nof many kinds of mandatory access control policies, including those\nbased on the concepts of Type Enforcement®, Role-based Access\nControl, and Multi-level Security.\n\npolicycoreutils contains the policy core utilities that are required\nfor basic operation of a SELinux system.  These utilities include\nload_policy to load policies, setfiles to label filesystems, newrole\nto switch roles, and run_init to run /etc/init.d scripts in the proper\ncontext.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
