INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (965,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','parted','x86_64','2264791','2.1','19.el6','2013-11-27 23:52:37','The GNU disk partition manipulation program','The GNU Parted program allows you to create, destroy, resize, move,\nand copy hard disk partitions. Parted can be used for creating space\nfor new operating systems, reorganizing disk usage, and copying data\nto new hard disks.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
