INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (967,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','xerces-j2','x86_64','8965475','2.7.1','12.6.el6_0','2013-11-27 23:51:31','Java XML parser','Welcome to the future! Xerces2 is the next generation of high\nperformance, fully compliant XML parsers in the Apache Xerces family.\nThis new version of Xerces introduces the Xerces Native Interface (XNI),\na complete framework for building parser components and configurations\nthat is extremely modular and easy to program.\n\nThe Apache Xerces2 parser is the reference implementation of XNI but\nother parser components, configurations, and parsers can be written\nusing the Xerces Native Interface. For complete design and\nimplementation documents, refer to the XNI Manual.\n\nXerces 2 is a fully conforming XML Schema processor. For more\ninformation, refer to the XML Schema page.\n\nXerces 2 also provides a partial implementation of Document Object Model\nLevel 3 Core, Load and Save and Abstract Schemas [deprecated] Working\nDrafts. For more information, refer to the DOM Level 3 Implementation\npage.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (968,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gettext','x86_64','6371313','0.17','16.el6','2013-11-27 23:50:02','GNU libraries and utilities for producing multi-lingual messages','The GNU gettext package provides a set of tools and documentation for\nproducing multi-lingual messages in programs. Tools include a set of\nconventions about how programs should be written to support message\ncatalogs, a directory and file naming organization for the message\ncatalogs, a runtime library which supports the retrieval of translated\nmessages, and stand-alone programs for handling the translatable and\nthe already translated strings. Gettext provides an easy to use\nlibrary and tools for creating, using, and modifying natural language\ncatalogs and is a powerful and simple method for internationalizing\nprograms.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (969,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gvfs-archive','x86_64','143647','1.4.3','15.el6','2013-11-27 23:54:13','Archiving support for gvfs','This package provides support for accessing files inside Zip and Tar archives,\nas well as ISO images, to applications using gvfs.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (970,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','samba-winbind-clients','x86_64','6836879','3.6.9','151.el6','2013-11-27 23:52:39','Samba winbind clients','The samba-winbind-clients package provides the NSS library and a PAM\nmodule necessary to communicate to the Winbind Daemon\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
