INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (972,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','perl-Log-Message','x86_64','42567','0.02','129.el6','2013-11-27 23:49:25','Generic message storage mechanism','Log::Message is a generic message storage mechanism. It allows you to\nstore messages on a stack -- either shared or private -- and assign meta-data\nto it. Some meta-data will automatically be added for you, like a timestamp\nand a stack trace, but some can be filled in by the user, like a tag by\nwhich to identify it or group it, and a level at which to handle the\nmessage (for example, log it, or die with it).\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (973,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libwnck','x86_64','1858284','2.28.0','3.el6','2013-11-27 23:51:36','Window Navigator Construction Kit','\nlibwnck (pronounced \"libwink\") is used to implement pagers, tasklists,\nand other such things. It allows applications to monitor information\nabout open windows, workspaces, their names/icons, and so forth.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
