INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (975,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','gedit','x86_64','14256330','2.28.4','3.el6','2013-11-27 23:54:14','Text editor for the GNOME desktop','gedit is a small, but powerful text editor designed specifically for\nthe GNOME desktop. It has most standard text editor functions and fully\nsupports international text in Unicode. Advanced features include syntax\nhighlighting and automatic indentation of source code, printing and editing\nof multiple documents in one window.\n\ngedit is extensible through a plugin system, which currently includes\nsupport for spell checking, comparing files, viewing CVS ChangeLogs, and\nadjusting indentation levels. Further plugins can be found in the\ngedit-plugins package.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
