INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (977,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','perl-NetAddr-IP','x86_64','256467','4.027','7.el6','2014-05-14 13:36:36','Manages IPv4 and IPv6 addresses and subnets','This module provides an object-oriented abstraction on top of IP addresses\nor IP subnets, that allows for easy manipulations. Version 4.xx of\nNetAdder::IP will will work older versions of Perl and does not use\nMath::BigInt as in previous versions.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
