INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (979,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','perl-Net-DNS','x86_64','488374','0.65','5.el6','2014-05-14 13:36:39','DNS resolver modules for Perl','Net::DNS is a collection of Perl modules that act as a Domain Name\nSystem (DNS) resolver. It allows the programmer to perform DNS queries\nthat are beyond the capabilities of gethostbyname and gethostbyaddr.\n\nThe programmer should be somewhat familiar with the format of a DNS\npacket and its various sections. See RFC 1035 or DNS and BIND (Albitz\n& Liu) for details.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
