INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (982,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','libsoup-devel','x86_64','1568236','2.28.2','4.el6','2013-11-27 23:52:41','Header files for the Soup library','Libsoup is an HTTP library implementation in C. This package allows\nyou to develop applications that use the libsoup library.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (983,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','liboil','x86_64','593601','0.3.16','4.1.el6','2013-11-27 23:49:26','Library of Optimized Inner Loops, CPU optimized functions','Liboil is a library of simple functions that are optimized for various CPUs.\nThese functions are generally loops implementing simple algorithms, such as\nconverting an array of N integers to floating-poing numbers or multiplying\nand summing an array of N numbers. Clearly such functions are candidates for\nsignificant optimization using various techniques, especially by using\nextended instructions provided by modern CPUs (Altivec, MMX, SSE, etc.).\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (984,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','glibc','i686','13790902','2.12','1.132.el6','2014-02-17 13:54:21','The GNU libc libraries','The glibc package contains standard libraries which are used by\nmultiple programs on the system. In order to save disk space and\nmemory, as well as to make upgrading easier, common system code is\nkept in one place and shared between programs. This particular package\ncontains the most important sets of shared libraries: the standard C\nlibrary and the standard math library. Without these two libraries, a\nLinux system will not function.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (985,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','evolution-data-server','x86_64','12617039','2.28.3','16.el6','2013-11-27 23:51:38','Backend data server for Evolution','The evolution-data-server package provides a unified backend\nfor programs that work with contacts, tasks, and calendar information.\n\nIt was originally developed for Evolution (hence the name), but is now used\nby other packages.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (986,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','un-core-fonts-common','noarch','19115','1.0.2','0.15.080608.el6','2013-11-27 23:48:57','Common files of Un Core fonts','\nThe UN set of Korean TrueType fonts is derived from the HLaTeX Type1 fonts\nmade by Koaunghi Un in 1998. They were converted to TrueType with\nFontForge(PfaEdit) by Won-kyu Park in 2003.\nThe Un Core set is composed of:\n\n- UnBatang: serif\n- UnDinaru: fantasy\n- UnDotum: sans-serif\n- UnGraphic: sans-serif style\n- UnGungseo: cursive, brush-stroke\n- UnPilgi: script\n\nThis package consists of files used by other un-core-fonts packages.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
