INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (990,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','perl-Log-Message-Simple','x86_64','10808','0.04','129.el6','2013-11-27 23:49:26','Simplified frontend to Log::Message','This module provides standardized logging facilities using the\nLog::Message module.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (991,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','collectd','x86_64','1392119','4.10.9','1.el6','2014-05-14 13:36:34','Statistics collection daemon for filling RRD files','collectd is a small daemon written in C for performance.  It reads various\nsystem  statistics  and updates  RRD files,  creating  them if necessary.\nSince the daemon doesn\'t need to startup every time it wants to update the\nfiles it\'s very fast and easy on the system. Also, the statistics are very\nfine grained since the files are updated every 10 seconds.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (992,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','jakarta-commons-codec','x86_64','248005','1.3','11.7.el6','2013-11-27 23:51:41','Implementations of common encoders and decoders','Commons Codec is an attempt to provide definitive implementations of\ncommonly used encoders and decoders. Examples include Base64, Hex,\nPhonetic and URLs.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
INSERT INTO `os_package_tbl` (`PKG_ID`,`MACHINE_ID`,`NAME`,`ARCH`,`SIZE`,`VERSION`,`RELEASE_INFO`,`INSTALL_DATE`,`SUMMARY`,`DESCRIPTION`,`REG_USER_ID`,`REG_DT`,`UPD_USER_ID`,`UPD_DT`) VALUES (993,'8aadaf9e-1464-4b4d-afd0-001c424e66f7','kernel-headers','x86_64','2599381','2.6.32','358.el6','2013-11-27 23:48:59','Header files for the Linux kernel for use by glibc','Kernel-headers includes the C header files that specify the interface\nbetween the Linux kernel and userspace libraries and programs.  The\nheader files define structures and constants that are needed for\nbuilding most standard programs and are also needed for rebuilding the\nglibc package.\n',NULL,'2014-08-19 15:20:53',NULL,'2014-08-19 15:20:53');
