Ext.define('MyApp.view.AlmProjectWindow', {
    extend: 'Ext.window.Window',
    alias: 'widget.AlmProjectWindow',

    requires: [
        'MyApp.view.MyForm33',
        'Ext.form.Panel',
        'Ext.toolbar.Toolbar',
        'Ext.button.Button'
    ],

    height: 270,
    id: 'AlmProjectWindow',
    itemId: 'AlmProjectWindow',
    width: 400,
    resizable: false,
    layout: 'border',
    title: 'Create Project',
    modal: true,

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'panel',
                    flex: 1,
                    region: 'center',
                    header: false,
                    title: 'projectPanel',
                    items: [
                        {
                            xtype: 'myform33'
                        }
                    ],
                    dockedItems: [
                        {
                            xtype: 'toolbar',
                            dock: 'bottom',
                            ui: 'footer',
                            layout: {
                                type: 'hbox',
                                pack: 'center'
                            },
                            items: [
                                {
                                    xtype: 'button',
                                    handler: function(button, e) {
                                        var projectForm = Ext.getCmp("addProjectForm");

                                        if(projectForm.isValid()) {

                                            Ext.Ajax.request({
                                                url: GLOBAL.urlPrefix + "alm/project",
                                                method: 'POST',
                                                headers: { 'Content-Type': 'application/json' },
                                                waitMsg: 'Saving Data...',
                                                jsonData: projectForm.getForm().getFieldValues(),
                                                success: function (response) {

                                                    var responseData = Ext.JSON.decode(response.responseText);

                                                    if(responseData.success) {

                                                        Ext.Msg.alert('Success', responseData.msg);

                                                        Ext.getCmp('almProjectGrid').getStore().reload();

                                                        projectForm.up('window').close();

                                                    } else {

                                                        Ext.Msg.alert('Failure', responseData.msg);

                                                    }

                                                },
                                                failure: function (response) {
                                                    var msg = Ext.JSON.decode(response.responseText).msg;

                                                    Ext.Msg.alert('Failure', msg);
                                                }
                                            });

                                        }

                                    },
                                    margin: '0 15 0 0',
                                    padding: '2 5 2 5',
                                    text: 'Create'
                                },
                                {
                                    xtype: 'button',
                                    handler: function(button, e) {
                                        Ext.MessageBox.confirm('Confirm', '작업을 취소하시겠습니까?', function(btn){

                                            if(btn == "yes"){
                                                button.up("window").close();
                                            }

                                        });

                                    },
                                    margin: '0 0 0 0',
                                    padding: '2 5 2 5',
                                    text: 'Cancel'
                                }
                            ]
                        }
                    ]
                }
            ]
        });

        me.callParent(arguments);
    }

});